function stopResumeTimer(e,t){if(t.innerHTML==="Start"){document.getElementById(e).getElementsByTagName("timer")[0].start();document.getElementById("title").start();t.innerHTML="Stop";t.className=t.className+" active"}else if(t.innerHTML==="Stop"){document.getElementById(e).getElementsByTagName("timer")[0].stop();document.getElementById("title").stop();t.innerHTML="Resume";t.className="";t.className="btn btn-default btn-lg"}else{document.getElementById(e).getElementsByTagName("timer")[0].resume();document.getElementById("title").resume();t.innerHTML="Stop";t.className=t.className+" active"}}angular.module("tracker",["timer"]).controller("Timer",["$scope",function(e){}]);